import React from 'react'
import { useFormik } from "formik"
import * as Yup from "yup"

import { Container } from '../Styles/StyledComponent'

const Login = () => {

  const getInitialValues = () => ({
    email: "",
    password: ""
  })

  const getValidationSchema = () => (
    Yup.lazy(() =>

      Yup.object().shape({
        email: Yup.string().email().required("Campo obligatorio"),
        password: Yup.string().min(8, "La contrase単a debe tener al menos 8 caracteres").max(20, "La contrase単a no debe superar los 20 caracteres").required("Campo obligatorio")
      })
    )
  )

  const onSubmit = (values) => {
    console.log(values);
  }

  const { handleSubmit, values, setFieldValue, errors } = useFormik({
    validateOnBlur: false,
    validateOnChange: false,
    initialValues: getInitialValues(),
    validationSchema: getValidationSchema(),
    onSubmit
  })


  return (
    <Container>
      <form action="" onSubmit={handleSubmit}>
        <label>Email:</label>
        <input type="email" name="email" id="email" onChange={(e) => setFieldValue("email", e.target.value)}/>

        <label>Password:</label>
        <input type="password" name="password" id="password" onChange={(e) => setFieldValue("password", e.target.value)} />
      </form>
    </Container>
  )
}

export default Login
